using SMC.Inscricoes.Common;
using SMC.Framework.Fake;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Reflection;
using System.Web;
using SMC.DadosMestres.Common.Areas.PES.Enums;
using SMC.Framework.Model;
using SMC.GPI.Inscricao.Areas.INS.Models;
using SMC.GPI.Inscricao.Models;
using SMC.Localidades.UI.Mvc.Models;
using SMC.Formularios.UI.Mvc.Model;

namespace SMC.GPI.Inscricao.Areas.INS.Fake
{
    public class CodigoAutorizacaoStrategy : SMCFakeStrategyBase
    {
        /// <summary>
        /// Define a prioridade da estratégia de fake
        /// </summary>
        public override int Priority
        {
            get { return 99; }
        }


        protected override bool CheckType(Type type)
        {
            return type == typeof(PaginaCodigoAutorizacaoViewModel);
        }

        protected override object CreateType(Type type)
        {
            return Gerar();
        }

        private PaginaCodigoAutorizacaoViewModel Gerar()
        {
            var pagina = new PaginaCodigoAutorizacaoViewModel();

            pagina.Titulo = "Código(s) de Autorização";
            pagina.LabelCodigoAutorizacao = "Código(s) de Autorização";

            var secaoInstrucoes = new TemplateSecaoPaginaTextoViewModel();
            secaoInstrucoes.Token = TOKENS.SECAO_INSTRUCOES;
            secaoInstrucoes.Texto = "<p>Informe o(s) código(s) de autorização para prosseguir.</p>";
            pagina.Secoes.Add(secaoInstrucoes);

            /*
            var arqs = new List<ArquivoSecaoViewModel>();
            var arq1 = new ArquivoSecaoViewModel();
            arq1.Arquivo = new SMCUploadFile();
            arq1.Descricao = "Acesse o link abaixo para consultar a primeira parte do edital do processo de inscrição:";
            arq1.NomeLink = "Edital_Inscrições IEC 2º/2014 - parte 01";
            arqs.Add(arq1);
            */
            pagina.Secoes.Add(new TemplateSecaoPaginaArquivoViewModel()
            {
                // Arquivos = arqs,
                Token = TOKENS.SECAO_ARQUIVOS
            });

            //painel Lateral
            pagina.ImagemCabecalho = "";
            pagina.DescricaoProcesso = "Seleção Pós-Graduação IEC 2-2015";
            pagina.DescricaoGrupoOferta = "Master";
            if (pagina.DescricaoOfertas.Count == 0)
                pagina.DescricaoOfertas.Add("Master em Business Inteligence");
            else
                pagina.DescricaoOfertas[0] = "Master em Business Inteligence";
            pagina.Ordem = 40;

            //Criação dos passos
            pagina.FluxoPaginas = new List<ITemplateFluxoPagina>();
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 10,
                Titulo = "Instruções Iniciais",
                Token = TOKENS.PAGINA_INSTRUCOES_INICAIS
            });
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 20,
                Titulo = "Confirmação de Dados",
                Token = TOKENS.PAGINA_CONFIRMACAO_DADOS_INSCRITO
            });
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 30,
                Titulo = "Seleção de Oferta",
                Token = TOKENS.PAGINA_SELECAO_OFERTA
            });
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 40,
                Titulo = "Código(s) de Autorização",
                Token = TOKENS.PAGINA_CODIGO_AUTORIZACAO
            });
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 50,                
                Titulo = "Formulário de Inscrição",
                Token = TOKENS.PAGINA_FORMULARIO_INSCRICAO
            });
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 60,
                Titulo = "Upload de Documentos",
                Token = TOKENS.PAGINA_UPLOAD_DOCUMENTOS
            });
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 70,
                Titulo = "Confirmação Inscrição",
                Token = TOKENS.PAGINA_CONFIRMACAO_INSCRICAO
            });
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 80,
                Titulo = "Instruções Finais",
                Token = TOKENS.PAGINA_INSTRUCOES_FINAIS
            });
            pagina.FluxoPaginas.Add(new FluxoPaginaViewModel
            {
                Ordem = 90,
                Titulo = "Comprovante",
                Token = TOKENS.PAGINA_COMPROVANTE_INSCRICAO
            });

            return pagina;
        }
    }
}